$env_node_path=$env:NODE_PATH
$new_node_path="D:\Proyectos\LADETEC\frontend\node_modules\.pnpm\js2coffee@2.2.0\node_modules\js2coffee\bin\node_modules;D:\Proyectos\LADETEC\frontend\node_modules\.pnpm\js2coffee@2.2.0\node_modules\js2coffee\node_modules;D:\Proyectos\LADETEC\frontend\node_modules\.pnpm\js2coffee@2.2.0\node_modules;D:\Proyectos\LADETEC\frontend\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/Proyectos/LADETEC/frontend/node_modules/.pnpm/js2coffee@2.2.0/node_modules/js2coffee/bin/node_modules:/mnt/d/Proyectos/LADETEC/frontend/node_modules/.pnpm/js2coffee@2.2.0/node_modules/js2coffee/node_modules:/mnt/d/Proyectos/LADETEC/frontend/node_modules/.pnpm/js2coffee@2.2.0/node_modules:/mnt/d/Proyectos/LADETEC/frontend/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../js2coffee@2.2.0/node_modules/js2coffee/bin/js2coffee" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../js2coffee@2.2.0/node_modules/js2coffee/bin/js2coffee" $args
  }
  $ret=$LASTEXITCODE
